mapper.select:
select count(*)
from t_ordersetting
where orderDate = #{orderDate}

mapper.update:
update t_ordersetting
set number = #{number}
where orderDate = #{orderDate}

mapper.insert:
insert into t_ordersetting (orderDate, number, reservations)
values (
        #{orderDate}, #{number}, #{reservations}
        )

mapper.select:
select *
from t_ordersetting
where orderDate between #{startDate} and #{endDate}
